class _Drawer extends StatelessWidget{const _Drawer();
 @override Widget build(BuildContext c){return Drawer(child: ListView(children:[const DrawerHeader(child:Text('MediTour')),ListTile(leading:const Icon(Icons.home),title:const Text('Home'),onTap:()=>Navigator.pushReplacementNamed(c,'/home')),ListTile(leading:const Icon(Icons.chat),title:const Text('AI Voice Chatbot'),onTap:()=>Navigator.pushReplacementNamed(c,'/chat')),ListTile(leading:const Icon(Icons.video_call),title:const Text('Pre-travel Consultation'),onTap:()=>Navigator.pushReplacementNamed(c,'/consult')),ListTile(leading:const Icon(Icons.request_quote),title:const Text('Quotation Request'),onTap:()=>Navigator.pushReplacementNamed(c,'/quotation')),ListTile(leading:const Icon(Icons.medical_information),title:const Text('Doctors'),onTap:()=>Navigator.pushReplacementNamed(c,'/doctors')),ListTile(leading:const Icon(Icons.verified_user),title:const Text('Compliance'),onTap:()=>Navigator.pushReplacementNamed(c,'/compliance')),ListTile(leading:const Icon(Icons.event_available),title:const Text('Bookings'),onTap:()=>Navigator.pushReplacementNamed(c,'/bookings')),ListTile(leading:const Icon(Icons.person),title:const Text('Profile'),onTap:()=>Navigator.pushReplacementNamed(c,'/profile')),const Divider(),ListTile(leading:const Icon(Icons.logout),title:const Text('Logout'),onTap:()=>Navigator.pushNamedAndRemoveUntil(c,'/',(r)=>false)),],));}
